class Queue {
private:
    int* arr;
    int front, rear, size, capacity;
public:
    Queue(int cap) : front(0), rear(-1), size(0), capacity(cap) {
        arr = new int[capacity];
    }
    ~Queue() {
        delete[] arr;
    }
    void enqueue(int x) {
        if (size < capacity) {
            arr[++rear] = x;
            size++;
        }
    }
    int dequeue() {
        if (size > 0) {
            int x = arr[front++];
            size--;
            return x;
        }
        return -1;
    }
};

