fun dijkstra(graph: Map<Int, Map<Int, Int>>, start: Int): Map<Int, Int> {
    val distances = mutableMapOf(start to 0)
    val pq = PriorityQueue<Pair<Int, Int>> { a, b -> a.second - b.second }
    pq.add(start to 0)
    while (pq.isNotEmpty()) {
        val (node, dist) = pq.poll()
        if (distances.getOrDefault(node, Int.MAX_VALUE) < dist) continue
        graph[node]?.forEach { (neighbor, weight) ->
            val newDist = dist + weight
            if (newDist < distances.getOrDefault(neighbor, Int.MAX_VALUE)) {
                distances[neighbor] = newDist
                pq.add(neighbor to newDist)
            }
        }
    }
    return distances
}

