fn lru_cache(capacity: usize) -> HashMap<i32, i32> {
    let mut cache = HashMap::with_capacity(capacity);
    let mut order = VecDeque::with_capacity(capacity);
    return |key: i32, value: i32| {
        if cache.len() >= capacity && !cache.contains_key(&key) {
            if let Some(oldest) = order.pop_front() {
                cache.remove(&oldest);
            }
        }
        cache.insert(key, value);
        order.push_back(key);
        value
    };
}

