}

fn flood_fill(img: []u8, x: i32, y: i32, new_color: u8) void {
    const old_color = img[x * width + y];
    if (old_color == new_color) return;
    img[x * width + y] = new_color;
    if (x > 0) flood_fill(img, x - 1, y, new_color);
    if (x < height - 1) flood_fill(img, x + 1, y, new_color);
    if (y > 0) flood_fill(img, x, y - 1, new_color);
}

