bool isPalindrome(string s) {
    int left = 0, right = cast(int)s.length - 1;
    while (left < right) {
        if (s[left] != s[right]) return false;
        left++;
        right--;
    }
    return true;
}

fn matrix_multiply(a: []i32, b: []i32, rows_a: i32, cols_a: i32, cols_b: i32) []i32 {
    var result = []i32{len: rows_a * cols_b, init: 0}
    for i := 0; i < rows_a; i += 1 {
        for j := 0; j < cols_b; j += 1 {
            for k := 0; k < cols_a; k += 1 {
                result[i * cols_b + j] += a[i * cols_a + k] * b[k * cols_b + j]
            }
        }
    }
    return result
}

